import React, { useState } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:8000"; // Replace with your actual backend URL if different

const Login = ({ onLogin }) => { // Accept onLogin as a prop
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(""); // Clear previous messages

    // Format data as required by the backend (x-www-form-urlencoded)
    const loginData = new URLSearchParams();
    loginData.append("username", formData.email); // "username" corresponds to OAuth2PasswordRequestForm
    loginData.append("password", formData.password);

    try {
      const response = await axios.post(`${API_BASE_URL}/users/login`, loginData, {
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
      });
      console.log(response.data); // Log the response data
      await onLogin({ email: formData.email, password: formData.password }); // Call onLogin with credentials
      setMessage("Login successful!");
    } catch (error) {
      console.error("Login error:", error); // Log the error for debugging
      const errorDetail =
        error.response?.data?.detail || "An error occurred. Please try again.";
      setMessage(errorDetail);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
