import React, { useEffect, useState } from "react";
import axios from "axios";
import "../css/Events.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);
  const [actionEvent, setActionEvent] = useState(null); // Event for registration/update
  const [actionType, setActionType] = useState(""); // Register or Update
  const [eventDetails, setEventDetails] = useState({}); // Form data for update
  const [message, setMessage] = useState(""); // Success/Error message
  const [volunteerCount, setVolunteerCount] = useState(""); // Volunteer count input

  const userType = localStorage.getItem("userType");

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        const response = await axios.get("http://localhost:8000/events", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEvents(response.data);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
      }
    };

    fetchEvents();
  }, []);

  const handleButtonClick = (event) => {
    if (userType === "individual" || userType === "university_club") {
      // Register functionality
      setActionEvent(event);
      setActionType("register");
    } else if (userType === "ministry") {
      // Update functionality
      setActionEvent(event);
      setActionType("update");
      setEventDetails({
        title: event.title,
        location: event.location,
        date: new Date(event.date).toISOString().slice(0, 10),
        max_volunteers: event.max_volunteers,
      });
    }
  };

  const handleRegisterSubmit = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await axios.post(
        `http://localhost:8000/events/${actionEvent.event_id}/register`,
        { volunteer_count: parseInt(volunteerCount) },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("Successfully registered for the event!");
      setActionEvent(null);
      setVolunteerCount("");
    } catch (err) {
      setMessage(
        err.response?.data?.detail || "Failed to register. Please try again."
      );
    }
  };

  const handleUpdateSubmit = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await axios.put(
        `http://localhost:8000/events/${actionEvent.event_id}`,
        eventDetails,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("Event updated successfully!");
      setActionEvent(null);
      setEventDetails({});
      // Refresh events list
      const updatedEvents = events.map((ev) =>
        ev.event_id === response.data.id ? response.data : ev
      );
      setEvents(updatedEvents);
    } catch (err) {
      setMessage(
        err.response?.data?.detail || "Failed to update event. Please try again."
      );
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  return (
    <div className="events-container">
      <h2>Available Events</h2>
      {error && <p className="error">{error}</p>}
      <div className="event-list">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event.event_id} className="event-card">
              <h3>{event.title}</h3>
              <p>
                <strong>Location:</strong> {event.location}
              </p>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(event.date).toLocaleDateString()}
              </p>
              <p>
                <strong>Volunteers:</strong> {event.max_volunteers}
              </p>
              <p>{event.description}</p>
              <button
                className="action-btn"
                onClick={() => handleButtonClick(event)}
              >
                {userType === "ministry" ? "Update" : "Register"}
              </button>
            </div>
          ))
        ) : (
          <p>No events available at the moment.</p>
        )}
      </div>

      {actionEvent && actionType === "register" && (
        <div className="registration-modal">
          <h3>Register for {actionEvent.title}</h3>
          <label>
            Volunteer Count:
            <input
              type="number"
              value={volunteerCount}
              onChange={(e) => setVolunteerCount(e.target.value)}
              min="1"
              max={actionEvent.max_volunteers}
            />
          </label>
          <div className="modal-actions">
            <button className="submit-btn" onClick={handleRegisterSubmit}>
              Submit
            </button>
            <button
              className="cancel-btn"
              onClick={() => setActionEvent(null)}
            >
              Cancel
            </button>
          </div>
          {message && <p className="message">{message}</p>}
        </div>
      )}

      {actionEvent && actionType === "update" && (
        <div className="update-modal">
          <h3>Update Event</h3>
          <label>
            Title:
            <input
              type="text"
              name="title"
              value={eventDetails.title}
              onChange={handleChange}
            />
          </label>
          <label>
            Location:
            <input
              type="text"
              name="location"
              value={eventDetails.location}
              onChange={handleChange}
            />
          </label>
          <label>
            Date:
            <input
              type="date"
              name="date"
              value={eventDetails.date}
              onChange={handleChange}
            />
          </label>
          <label>
            Max Volunteers:
            <input
              type="number"
              name="max_volunteers"
              value={eventDetails.max_volunteers}
              onChange={handleChange}
              min="1"
            />
          </label>
          <div className="modal-actions">
            <button className="submit-btn" onClick={handleUpdateSubmit}>
              Submit
            </button>
            <button
              className="cancel-btn"
              onClick={() => setActionEvent(null)}
            >
              Cancel
            </button>
          </div>
          {message && <p className="message">{message}</p>}
        </div>
      )}
    </div>
  );
};

export default Events;
