import React, { useEffect, useState } from "react";
import axios from "axios";
import "../css/Events.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);
  const [registrationEvent, setRegistrationEvent] = useState(null); // Event to register for
  const [volunteerCount, setVolunteerCount] = useState(""); // Volunteer count input
  const [registrationMessage, setRegistrationMessage] = useState(""); // Success/Error message

  const userType = localStorage.getItem("userType");

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        const response = await axios.get("http://localhost:8000/events", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEvents(response.data);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
      }
    };

    fetchEvents();
  }, []);

  const handleRegisterClick = (event) => {
    if (userType !== "individual" && userType !== "university_club") {
      setRegistrationMessage("Only individuals and university clubs can register.");
      return;
    }
    setRegistrationEvent(event);
  };

  const handleRegisterSubmit = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await axios.post(
        `http://localhost:8000/events/${registrationEvent.event_id}/register`,
        { volunteer_count: parseInt(volunteerCount) },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setRegistrationMessage("Successfully registered for the event!");
      setRegistrationEvent(null);
      setVolunteerCount("");
    } catch (err) {
      setRegistrationMessage(
        err.response?.data?.detail || "Failed to register. Please try again."
      );
    }
  };

  return (
    <div className="events-container">
      <h2>Available Events</h2>
      {error && <p className="error">{error}</p>}
      <div className="event-list">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event.event_id} className="event-card">
              <h3>{event.title}</h3>
              <p>
                <strong>Location:</strong> {event.location}
              </p>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(event.date).toLocaleDateString()}
              </p>
              <p>
                <strong>Volunteers:</strong> {event.max_volunteers}
              </p>
              <p>{event.description}</p>
              {userType === "ministry" ? (
                <button
                  className="update-btn"
                  onClick={() => handleUpdateClick(event)}
                >
                  Update
                </button>
              ) : (
                <button
                  className="volunteer-btn"
                  onClick={() => handleRegisterClick(event)}
                >
                  Register
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No events available at the moment.</p>
        )}
      </div>

      {updateEvent && (
        <div className="update-modal">
          <h3>Update Event</h3>
          <label>
            Title:
            <input
              type="text"
              name="title"
              value={eventDetails.title}
              onChange={handleChange}
            />
          </label>
          <label>
            Location:
            <input
              type="text"
              name="location"
              value={eventDetails.location}
              onChange={handleChange}
            />
          </label>
          <label>
            Date:
            <input
              type="date"
              name="date"
              value={eventDetails.date}
              onChange={handleChange}
            />
          </label>
          <label>
            Max Volunteers:
            <input
              type="number"
              name="max_volunteers"
              value={eventDetails.max_volunteers}
              onChange={handleChange}
              min="1"
            />
          </label>
          <div className="modal-actions">
            <button className="submit-btn" onClick={handleUpdateSubmit}>
              Submit
            </button>
            <button className="cancel-btn" onClick={() => setUpdateEvent(null)}>
              Cancel
            </button>
          </div>
          {updateMessage && <p className="message">{updateMessage}</p>}
        </div>
      )}
    </div>
  );
};

export default Events;
