import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:8000"; // Replace with your backend's URL

// Register user
export const registerUser  = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/users/register`, userData);
    return response.data;
  } catch (error) {
    throw error.response?.data?.detail || "Registration failed";
  }
};

// Login user
export const loginUser  = async (credentials) => {
  try {
      const loginData = new URLSearchParams();
      loginData.append("username", credentials.email); // Ensure this matches your backend
      loginData.append("password", credentials.password);

      const response = await axios.post(`${API_BASE_URL}/users/login`, loginData, {
          headers: { "Content-Type": "application/x-www-form-urlencoded" },
      });
      console.log("Login response:", response.data); // Log the response data
      return response.data;
  } catch (error) {
      console.error("Login error:", error); // Log the error for debugging
      throw error.response?.data?.detail || "Login failed";
  }
};
