import React, { useState } from "react";
import loginUser from "../api";

const Login = ({ onLogin }) => { // Accept onLogin as a prop
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(""); // Clear previous messages

    try {
        const response = await loginUser (formData); // Call the loginUser  function
        console.log("Login successful:", response); // Log the successful response
        setToken(response.access_token); // Set the token
        setMessage("Login successful!"); // Set success message
    } catch (error) {
        console.error("Login error:", error); // Log the error for debugging
        setMessage(error); // Set the error message
    }
};

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
