import React, { useEffect, useState } from "react";
import axios from "axios";
import "../css/Events.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);
  const [updateEvent, setUpdateEvent] = useState(null); // Event to update
  const [eventDetails, setEventDetails] = useState({}); // Form data for update
  const [updateMessage, setUpdateMessage] = useState(""); // Success/Error message

  const userType = localStorage.getItem("userType");

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        const response = await axios.get("http://localhost:8000/events", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEvents(response.data);
      } catch (err) {
        setError("Failed to fetch events. Please try again.");
      }
    };

    fetchEvents();
  }, []);

  const handleUpdateClick = (event) => {
    setUpdateEvent(event);
    setEventDetails({
      title: event.title,
      location: event.location,
      date: new Date(event.date).toISOString().slice(0, 10),
      max_volunteers: event.max_volunteers,
    });
  };

  const handleUpdateSubmit = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await axios.put(
        `http://localhost:8000/events/${updateEvent.event_id}`,
        eventDetails,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setUpdateMessage("Event updated successfully!");
      setUpdateEvent(null);
      setEventDetails({});
      // Refresh events list
      const updatedEvents = events.map((ev) =>
        ev.event_id === response.data.id ? response.data : ev
      );
      setEvents(updatedEvents);
    } catch (err) {
      setUpdateMessage(
        err.response?.data?.detail || "Failed to update event. Please try again."
      );
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  return (
    <div className="events-container">
      <h2>Available Events</h2>
      {error && <p className="error">{error}</p>}
      <div className="event-list">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event.event_id} className="event-card">
              <h3>{event.title}</h3>
              <p>
                <strong>Location:</strong> {event.location}
              </p>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(event.date).toLocaleDateString()}
              </p>
              <p>
                <strong>Volunteers:</strong> {event.max_volunteers}
              </p>
              <p>{event.description}</p>
              {userType === "ministry" ? (
                <button
                  className="update-btn"
                  onClick={() => handleUpdateClick(event)}
                >
                  Update
                </button>
              ) : (
                <button
                  className="volunteer-btn"
                  onClick={() => handleRegisterClick(event)}
                >
                  Register
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No events available at the moment.</p>
        )}
      </div>

      {updateEvent && (
        <div className="update-modal">
          <h3>Update Event</h3>
          <label>
            Title:
            <input
              type="text"
              name="title"
              value={eventDetails.title}
              onChange={handleChange}
            />
          </label>
          <label>
            Location:
            <input
              type="text"
              name="location"
              value={eventDetails.location}
              onChange={handleChange}
            />
          </label>
          <label>
            Date:
            <input
              type="date"
              name="date"
              value={eventDetails.date}
              onChange={handleChange}
            />
          </label>
          <label>
            Max Volunteers:
            <input
              type="number"
              name="max_volunteers"
              value={eventDetails.max_volunteers}
              onChange={handleChange}
              min="1"
            />
          </label>
          <div className="modal-actions">
            <button className="submit-btn" onClick={handleUpdateSubmit}>
              Submit
            </button>
            <button className="cancel-btn" onClick={() => setUpdateEvent(null)}>
              Cancel
            </button>
          </div>
          {updateMessage && <p className="message">{updateMessage}</p>}
        </div>
      )}
    </div>
  );
};

export default Events;
