import React, { useState } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:8000"; // Replace with your actual backend URL if different

const Login = ({ onLogin }) => { // Accept onLogin as a prop
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(""); // Clear previous messages
  
    try {
      // Call the loginUser  function with the form data
      const response = await loginUser (formData);
      console.log(response); // Log the response data
      setToken(response.access_token);
      setMessage("Login successful!");
    } catch (error) {
      console.error("Login error:", error); // Log the error for debugging
      setMessage(error); // Set the error message
    }
  };


  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
