import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Dashboard from "./components/Dashboard"; // Ensure the path is correct

const App = () => {
  const [user, setUser ] = useState(null);

  // Check for an existing token on app load
  useEffect(() => {
    const token = localStorage.getItem("accessToken");
    const userType = localStorage.getItem("userType");
    if (token && userType) {
      setUser ({ token, userType });
    }
  }, []);

  // Handle login
  const handleLogin = async (credentials) => {
    try {
      const response = await loginUser (credentials); // Ensure loginUser  is defined correctly
      localStorage.setItem("accessToken", response.access_token);
      localStorage.setItem("userType", response.user_type);
      setUser ({ token: response.access_token, userType: response.user_type });
    } catch (error) {
      console.error("Login failed:", error); // Log the error for debugging
      throw error; // Rethrow the error to be caught in the Login component
    }
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    localStorage.removeItem("userType");
    setUser (null);
  };

  return (
    <Router>
 <div>
        <nav>
          <h1>Reforestation Platform</h1>
          {user ? (
            <button onClick={handleLogout}>Logout</button>
          ) : (
            <div>
              <a href="/login">Login</a>
              <a href="/register">Register</a>
            </div>
          )}
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              user ? (
                <Dashboard user={user} />
              ) : (
                <Navigate to="/login" replace />
              )
            }
          />
          <Route
            path="/login"
            element={
              user ? (
                <Navigate to="/" replace />
              ) : (
                <Login onLogin={handleLogin} /> // Pass onLogin prop
              )
            }
          />
          <Route path="/register" element={<Register />} />
          <Route path="*" element={<h2>Page Not Found</h2>} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
