import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Events.css"; // Import CSS for styling

const Events = () => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        if (!token) {
          console.error("No token found in localStorage.");
          return;
        }
  
        console.log("Fetching events with token:", token);
  
        const response = await axios.get("http://localhost:8000/events", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
  
        console.log("Events fetched successfully:", response.data);
        setEvents(response.data);
      } catch (err) {
        console.error("Error fetching events:", err);
        setError("Failed to fetch events. Please try again.");
      }
    };
  
    fetchEvents();
  }, []);
  

  return (
    <div className="events-container">
      <h2>Available Events</h2>
      {error && <p className="error">{error}</p>}
      <div className="event-list">
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event.event_id} className="event-card">
              <h3>{event.title}</h3>
              <p><strong>Location:</strong> {event.location}</p>
              <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
              <p>
                <strong>Volunteers:</strong> {event.current_volunteers}/{event.max_volunteers}
              </p>
              <p>{event.description}</p>
              <button className="volunteer-btn">Volunteer Now</button>
            </div>
          ))
        ) : (
          <p>No events available at the moment.</p>
        )}
      </div>
    </div>
  );
};

export default Events;
